import { PropertyType, DealType, StatusType, RoomTypes, BathroomType, RenovationType } from '../constants/property-types';
/**
 * Estate apartment interface that matches NestJS/Mongoose EstateApartment schema
 */
export interface IApartment {
    _id?: string;
    identifier?: string;
    title: string;
    description?: string;
    address?: string;
    city?: string;
    country?: string;
    coordinates?: number[];
    price: number;
    price_sqm: number;
    price_per_month?: number;
    price_per_day?: number;
    tags?: string[];
    amenities?: string[];
    rooms?: RoomTypes;
    bathroom?: BathroomType;
    bathroomCount?: number;
    buildingYear?: number;
    buildingType?: string;
    buildingClass?: string;
    coastline?: string;
    contactPhone?: string;
    contactTelegram?: string;
    contactWhatsapp?: string;
    floor?: number;
    area?: number;
    livingArea?: number;
    kitchenArea?: number;
    balconyArea?: number;
    ceilingHeight?: number;
    renovation?: RenovationType;
    totalFloors?: number;
    propertyType?: PropertyType;
    isNewBuild?: boolean;
    isFromDeveloper?: boolean;
    dealType?: DealType;
    status?: StatusType;
    images?: string[];
    estate: string | object;
    building: string | object;
    author: string | object;
    developer?: string | object;
    oldDataAuthor?: string;
    oldDataId?: string;
    oldData?: string;
    analyticsViews?: number;
    analyticsFavorites?: number;
    commission?: number;
    createdAt?: Date | string;
    updatedAt?: Date | string;
    currency?: string;
    city_code?: string;
    country_code?: string;
    contactInfo?: string;
    district?: string;
    features?: string[];
    source?: string;
    sourceUrl?: string;
    sourcePostId?: any;
    sourceId?: string;
    isExported?: boolean;
}
